using System;
using System.IO;
using System.Net;
using System.Text;
using System.Drawing;
using System.Net.Http;
using Newtonsoft.Json;
using TradeSaber.Models;
using System.Threading.Tasks;
using TradeSaber.Models.Settings;

namespace TradeSaber.Services
{
    public class CardGenerator
    {
        private readonly HttpClient _client;
        private readonly IAPISettings _apiSettings;
        private readonly CardDispatcher _cardDispatcher;

        public CardGenerator(HttpClient client, IAPISettings settings, CardDispatcher cardDispatcher)
        {
            _client = client;
            _apiSettings = settings;
            _cardDispatcher = cardDispatcher;
        }

        public async Task<byte[]> Generate(string b64, Card card, Series series, string path)
        {
            string rarityColor = "white";
            switch (card.Rarity)
            {
                case Rarity.Common:
                    rarityColor = "white";
                    break;
                case Rarity.Uncommon:
                    rarityColor = "#26f1ff";
                    break;
                case Rarity.Rare:
                    rarityColor = "#d426ff";
                    break;
                case Rarity.Legendary:
                    rarityColor = "goldenrod";
                    break;
            }
            string finalHTML = GetFormattedCardHTML(series.MainColor, series.SubColor, b64, card.Name, card.Description, rarityColor, card.Rarity.ToString(), _font);
            HTIBody body = new HTIBody
            {
                width = 872,
                height = 1272,
                html = finalHTML
            };
            var response = await _client.PostAsync(_apiSettings.HTI, new StringContent(JsonConvert.SerializeObject(body), Encoding.UTF8, "application/json"));
            if (response.StatusCode == HttpStatusCode.OK)
            {
                byte[] bytes = await response.Content.ReadAsByteArrayAsync();
                return bytes;
            }
            return new byte[0];
        }


        /// <summary>
        /// This is very time consuming! Don't upload any new cards while this is running!
        /// </summary>
        public async void UpdateAllCardHTML(IProgress<float> updateProgress, string cardPath)
        {
            IProgress<float> progress = updateProgress;
            progress.Report(0.0f);

            Series[] allSeries = _cardDispatcher.GetAllSeries();

            float currentProgress = 0.0f;
            float seriesProgressSegments = 1f / allSeries.Length;

            string path = cardPath;

            for (int i = 0; i < allSeries.Length; i++)
            {
                Series currentSeries = allSeries[i];

                Card[] cards = _cardDispatcher.GetCardsFromSeries(currentSeries.Id);

                for (int q = 0; q < cards.Length; q++)
                {
                    Card card = cards[q];

                    string rarityColor = "white";
                    switch (card.Rarity)
                    {
                        case Rarity.Common:
                            rarityColor = "white";
                            break;
                        case Rarity.Uncommon:
                            rarityColor = "#26f1ff";
                            break;
                        case Rarity.Rare:
                            rarityColor = "#d426ff";
                            break;
                        case Rarity.Legendary:
                            rarityColor = "goldenrod";
                            break;
                    }
                    Image image = Image.FromFile(path + card.BaseURL);
                    using MemoryStream ms = new MemoryStream();
                    image.Save(ms, image.RawFormat);
                    byte[] imageBytes = ms.ToArray();
                    await ms.DisposeAsync();
                    var extension = Path.GetExtension(card.BaseURL);
                    string b64 = $"data:image/{extension.ToLower().Replace(".", "")};base64," + Convert.ToBase64String(imageBytes);

                    string finalHTML = GetFormattedCardHTML(currentSeries.MainColor, currentSeries.SubColor, b64, card.Name, card.Description, rarityColor, card.Rarity.ToString(), _font);

                    HTIBody body = new HTIBody
                    {
                        width = 872,
                        height = 1272,
                        html = finalHTML
                    };
                    var response = await _client.PostAsync(_apiSettings.HTI, new StringContent(JsonConvert.SerializeObject(body), Encoding.UTF8, "application/json"));
                    if (response.StatusCode == HttpStatusCode.OK)
                    {
                        byte[] bytes = await response.Content.ReadAsByteArrayAsync();
                        await File.WriteAllBytesAsync(path + card.CoverURL, bytes);
                    }
                }
                currentProgress += seriesProgressSegments;
                progress.Report(currentProgress);
            }

            progress.Report(1.0f);
        }

        public string GetFormattedCardHTML(string mainColor, string subColor, string cardBase64, string cardName, string cardDesc, string rarityColor, string cardRarity, string font)
        {
            return $"<!DOCTYPE html> <html> <head> <style> @font-face {{ font-family: \"tschicholdregular\"; src: url({font}) format(\"truetype\"); }} html {{ background-color: transparent; width: 800px; height: 1200px; font-family: 'tschicholdregular'; }} main {{ width: 800px; height: 1200px; padding: 25px; border-radius: 50px; background-color: {mainColor}; box-shadow: inset 0px 0px 5px 5px black; background-image: radial-gradient({subColor} 30%, transparent 16%), radial-gradient({subColor}, transparent 16%); background-size: 60px 60px; background-position: 0 0, 30px 30px; }} .content {{ box-sizing: content-box; }} .top {{ width: 800px; height: 110px; border-top-left-radius: 50px; border-top-right-radius: 50px; background-color: rgba(0, 0, 0, 0.925); box-sizing: content-box; margin-top: -85px; margin-bottom: 25px; box-shadow: inset 0px 0px 5px 5px black; line-height: 100px; text-align: center; font-size: 80px; }} .profile {{ width: 800px; height: 800px; background: url(\"{cardBase64}\"); background-color: white; background-position-y: center; background-position-x: center; background-size: 800px; margin-bottom: 25px; box-shadow: inset 0px 0px 5px 5px black; }} .bottom {{ width: 700px; height: 240px; border-bottom-left-radius: 50px; border-bottom-right-radius: 50px; background-color: rgba(0, 0, 0, 0.925); box-shadow: inset 0px 0px 5px 5px black; margin-top: 0px; padding-left: 50px; padding-right: 50px; padding-top: 10px; font-size: 50px; line-height: 50px; text-align: start; }} .top-text {{ color: white; letter-spacing: 3px; text-shadow: 2px 2px 10px white; }} .bottom-text {{ color: white; margin-top: 60px; }} .rarity-text {{ color: {rarityColor}; text-align: left; font-size: 50px; margin-top: -240px; padding-left: 50px; text-shadow: 2px 2px 10px {rarityColor}; }} </style> </head> <body> <main class=\"content\"> <div class=\"top\"> <p class=\"top-text\">{cardName}</p> </div> <div class=\"profile\"> </div> <div class=\"bottom\"> <p class=\"bottom-text\">{cardDesc}</p> </div> <p class=\"rarity-text\">{cardRarity}</p> </main> </body> </html>";
        }

        private readonly string _font = "data:font/truetype;charset=utf-8;base64,";
        public class HTIBody
        {
            public int width { get; set; }
            public int height { get; set; }
            public string html { get; set; }
        }
    }
}